def maxResult(nums, k):
    dp = [0] * k
    dp[0] = nums[0]
    queue = deque([0])
    queue.append(0)
    for i in range(1, len(nums)):
        last_processed = -1
        if i >= k or i < 0:
            continue
        queue.append(i)
        last_processed = i
        while queue:
            processed = queue.popleft()
            dp[processed] = max(dp[processed], nums[i])
            queue.append(processed)
            last_processed = processed
    return dp[k-1]

