reference_code:
def MinSize(A: List[int], n: int) -> int:
    #find majority element which may occur more than n/2 (Boyer-Moore Majority Voting Algorithm)
    curr = A[0]
    count = 1
    for i in range(1, n):
        if A[i] == curr:
            count += 1
        else:
            count -= 1
            if count == 0:
                curr = A[i]
                count = 1
    max_f = A.count(curr)
    if max_f <= (n + 1) // 2:
        return n & 1
    else:
        return 2 * max_f - n

