        diff = [0] * (to_max + 1)
        for num_i, from_i, to_i in trips:
            diff[from_i] += num_i
            diff[to_i] -= num_i
        count = 0
        for i in range(to_max + 1):
            count += diff[i]
            if count > capacity:
                return False
        return True

