def minPathCost( grid: List[List[int]], moveCost: List[List[int]]) -> int:
       m, n = len(grid), len(grid[0])
       dp = grid[0]
       for i in range(1, len(grid)):
           dp = [grid[i][j] + min(dp[k] + moveCost[grid[i - 1][k]][j] for k in range(n)) for j in range(n)]
       return min(dp)

