def collectTheCoins(coins, edges):
    queue = collections.deque()
    degree_values = [0] * len(coins)
    for i in range(len(coins)):
        if (coins[i] > 0 and i not in queue):
            queue.append(i)
        if (coins[i] == 1 and i not in queue):
            queue.append(i)
        elif (coins[i] < 0 and i in queue):
            queue.remove(i)
        degree_values[i] = degree_values[i] + 1
    return len(queue)

