                    continue
                kx, ky = king[0] + dx, king[1] + dy
                while 0 <= kx < 8 and 0 <= ky < 8:
                    if (kx, ky) in queen_pos:
                        ans.append([kx, ky])
                        break
                    kx += dx
                    ky += dy
        return ans

