reference_code:
def countPairs( n: int, edges: List[List[int]], queries: List[int]) -> List[int]:
        degree = [0 for _ in range(n)]
        cnt = collections.defaultdict(int)
        for edge in edges:
            x, y = edge[0] - 1, edge[1] - 1
            if x > y:
                x, y = y, x
            degree[x] += 1
            degree[y] += 1
            cnt[x * n + y] += 1
        arr = sorted(degree)
        ans = []
        for bound in queries:
            total = 0
            j = n - 1
            for i in range(n):
                while j > i and arr[i] + arr[j] > bound:
                    j -= 1
                total += n - 1 - max(i, j)
            for val, freq in cnt.items():
                x, y = val // n, val % n
                if degree[x] + degree[y] > bound and degree[x] + degree[y] - freq <= bound:
                    total -= 1
            ans.append(total)
        return ans

