def maxAbsoluteSum(nums):
    if not nums:
        return -1
    positiveSum = 0
    negativeSum = 0
    for num in nums:
        positiveSum += num
        negativeSum += num
    maxSum = positiveSum
    if negativeSum > 0:
        maxSum = negativeSum
    return maxSum

