public static int[] leftmostBuildingQueries(int[] heights, int[][] queries) {
        int n = heights.length;
        int m = queries.length;
        List<int[]>[] query = new List[n];
        for (int i = 0; i < n; i++) {
            query[i] = new ArrayList<int[]>();
        }
        int[] ans = new int[m];
        List<Integer> st = new ArrayList<Integer>();
        for (int i = 0; i < m; i++) {
            int a = queries[i][0];
            int b = queries[i][1];
            if (a > b) {
                int temp = a;
                a = b;
                b = temp;
            }
            if (a == b || heights[a] < heights[b]) {
                ans[i] = b;
                continue;
            }
            query[b].add(new int[]{i, heights[a]});
        }
        int top = -1;
        for (int i = n - 1; i >= 0; i--) {
            for (int j = 0; j < query[i].size(); j++) {
                int q = query[i].get(j)[0];
                int val = query[i].get(j)[1];
                if (top == -1 || heights[st.get(0)] <= val) {
                    ans[q] = -1;
                    continue;
                }
                int l = 0, r = top;
                while (l <= r) {
                    int mid = (l + r) >> 1;
                    if (heights[st.get(mid)] > val) {
                        l = mid + 1;
                    } else {
                        r = mid - 1;
                    }
                }
                ans[q] = st.get(r);
            }
            while (top >= 0 && heights[st.get(top)] <= heights[i]) {
                st.remove(st.size() - 1);
                top--;
            }
            st.add(i);
            top++;
        }
        return ans;
    }

