def mostFrequent(n,arr):
    dic={}
    for ele in arr:
        if ele in dic:
            dic[ele]+=1
        else:
            dic[ele]=1
    mx=0
    for ele in dic.values():
        mx=max(mx,ele)
    ans=0
    for key in sorted(dic):
        if dic[key]==mx:
            ans=key
    return ans

