def mostFrequent(a: list, n: int) -> int:
    frequency = {}
    for i in range(n):
        if frequency.get(a[i]):
            frequency[a[i]] += 1
        else:
            frequency[a[i]] = 1
    ans = max(frequency.values())
    for key, value in frequency.items():
        if key == ans and value > frequency[ans]:
            ans = key
            break
    return ans

