public static int getMaxProductivity(int n, int[] productivity) {
        int totalSum = 0;
        for (int val : productivity) {
            totalSum += val;
        }
        int halfSum = totalSum / 2;
        int[] dp = new int[halfSum + 1];
        dp[0] = 1;
        for (int prod : productivity) {
            for (int j = halfSum; j >= prod; j--) {
                dp[j] = dp[j] | dp[j - prod];
            }
        }
        int maxProductivity = 0;
        for (int i = 1; i <= halfSum; i++) {
            if (dp[i] == 1) {
                maxProductivity = Math.max(maxProductivity, i * (totalSum - i));
            }
        }
        return maxProductivity;
