public static int minSwaps(String S) {
        int evenSum = 0, oddSum = 0;
        int n = S.length();

        for (int i = 0; i < n; i++) {
            if (i % 2 == 1)
                oddSum += (S.charAt(i) == '1') ? 1 : 0;
            else
                evenSum += (S.charAt(i) == '1') ? 1 : 0;
        }

        int difference = Math.abs(oddSum - evenSum);

        if (difference % 2 == 1) return -1;

        return difference / 2;
    }

reference_code:
def minSwaps( S):
        even_sum, odd_sum = 0, 0
        n = len(S)
        # Calculating the sum of digits at even and odd positions.
        for i in range(n):
            if i % 2 == 1:
                odd_sum += (S[i] == '1')
            else:
                even_sum += (S[i] == '1')
        # Calculating the absolute difference between even and odd sums.
        difference = abs(odd_sum - even_sum)
        # If the difference is odd, return -1.
        if difference % 2 == 1:
            return -1
        # Returning half of the difference as the minimum swaps required.
        return difference // 2

