public static int[] removeOrder(int n, int[] arr, String str) {
        Arrays.sort(arr);
        int i = 0, j = n - 1;
        int[] ans = new int[n];
        int idx = 0;
        for (char ch : str.toCharArray()) {
            if (ch == '0')
                ans[idx++] = arr[i++];
            else
                ans[idx++] = arr[j--];
        }
        return ans;
    }

